import styled from 'styled-components'

export const InputContainer = styled.div`
    width: 100%;
    height: 150px;
    background-color: #AAAA;

    display: flex;
    align-items: center;
    justify-content: flex-end;

    font-size: 24px;
    font-family: 'Roboto';

    input {
        width: 100%;
        height: 150px;
        background-color: #AAAA;
        border: 0;
        align: right;
        padding: 0 10px;
        font-size: 55px;
        font-weight: 550;
        font-family: 'Roboto';
        color: #FFFFFF
    }

`
